import React, { useState, useEffect } from 'react';
import { useAuth } from '../../context/AuthContext';
import Loader from '../../components/Loader/Loader';
import { formatCurrency } from '../../utils/format';
import api from '../../utils/api';
import './Dashboard.css';

const Dashboard = () => {
  const { user } = useAuth();
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [stats, setStats] = useState({
    totalAccounts: 0,
    totalTransactions: 0,
    completedTransactions: 0,
    pendingTransactions: 0,
    failedTransactions: 0,
    totalCredits: 0,
    totalDebits: 0,
    recentTransactions: []
  });

  useEffect(() => {
    const fetchDashboardData = async () => {
      try {
        setLoading(true);
        const { data } = await api.get('/dashboard');
        setStats(data);
      } catch (error) {
        console.error('Error fetching dashboard data:', error);
        setError('Failed to load dashboard data');
      } finally {
        setLoading(false);
      }
    };

    fetchDashboardData();
  }, []);

  if (loading) {
    return <Loader fullPage message="Loading your dashboard..." />;
  }

  if (error) {
    return (
      <div className="error-message">
        <p>{error}</p>
      </div>
    );
  }

  return (
    <div className="dashboard">
      <h1>Welcome back, {user?.firstName}!</h1>

      <div className="stats-grid">
        <div className="stat-card">
          <h3>Accounts</h3>
          <p className="stat-value">{stats.totalAccounts}</p>
        </div>

        <div className="stat-card">
          <h3>Total Transactions</h3>
          <p className="stat-value">{stats.totalTransactions}</p>
        </div>

        <div className="stat-card success">
          <h3>Completed</h3>
          <p className="stat-value">{stats.completedTransactions}</p>
        </div>

        <div className="stat-card warning">
          <h3>Pending</h3>
          <p className="stat-value">{stats.pendingTransactions}</p>
        </div>

        <div className="stat-card danger">
          <h3>Failed</h3>
          <p className="stat-value">{stats.failedTransactions}</p>
        </div>

        <div className="stat-card total-amount">
          <h3>Total Credits</h3>
          <p className="stat-value">{formatCurrency(stats.totalCredits)}</p>
        </div>

        <div className="stat-card total-amount">
          <h3>Total Debits</h3>
          <p className="stat-value">{formatCurrency(stats.totalDebits)}</p>
        </div>
      </div>

      <div className="recent-activity">
        <h2>Recent Transactions</h2>
        {stats.recentTransactions?.length > 0 ? (
          <div className="activity-list">
            {stats.recentTransactions.map((tx) => (
              <div key={tx.id} className={`activity-item ${tx.status}`}>
                <div className="activity-icon">
                  {tx.status === 'completed' ? '✓' : tx.status === 'failed' ? '✗' : '⏳'}
                </div>
                <div className="activity-details">
                  <span className="activity-name">
                    {tx.description || 'Transaction'} ({tx.institution})
                  </span>
                  <span className={`activity-amount ${tx.transaction_type}`}>
                    {tx.transaction_type === 'credit' ? '+' : '-'}{formatCurrency(tx.amount)}
                  </span>
                </div>
                <span className="activity-date">
                  {tx.transaction_date ? new Date(tx.transaction_date).toLocaleDateString() : 'N/A'}
                </span>
              </div>
            ))}
          </div>
        ) : (
          <p className="no-activity">No recent transactions found</p>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
